using System;
using System.Data;using System.Text;using MyWeb.Data;namespace MyWeb.Business{	public class ConfigService	{		private static ConfigDAL db = new ConfigDAL();		#region[Config_GetById]		public static DataTable Config_GetById(string Id)		{			return db.Config_GetById(Id);		}		#endregion		#region[Config_GetByTop]
        public static DataTable Config_GetByTop(string Top, string Where, string Order)		{			return db.Config_GetByTop(Top, Where, Order);		}		#endregion		#region[Config_GetByAll]
        public static DataTable Config_GetByAll()		{			return db.Config_GetByAll();		}		#endregion		#region[Config_Insert]		public static bool Config_Insert(Config data)		{			return db.Config_Insert(data);		}		#endregion		#region[Config_Update]		public static bool Config_Update(Config data)		{			return db.Config_Update(data);		}		#endregion		#region[Config_Delete]		public static bool Config_Delete(string Id)		{			return db.Config_Delete(Id);		}		#endregion	}}