using System;using System.Collections.Generic;
using System.Data;using System.Text;using MyWeb.Data;namespace MyWeb.Business{	public class ContactService	{		private static ContactDAL db = new ContactDAL();		#region[Contact_GetById]		public static DataTable Contact_GetById(string Id)		{			return db.Contact_GetById(Id);		}		#endregion		#region[Contact_GetByTop]
        public static DataTable Contact_GetByTop(string Top, string Where, string Order)		{			return db.Contact_GetByTop(Top, Where, Order);		}		#endregion		#region[Contact_GetByAll]
