using System;using System.Collections.Generic;using System.Text;using MyWeb.Data;namespace MyWeb.Business{	public class ImagesService	{		private static ImagesDAL db = new ImagesDAL();		#region[Images_GetById]		public static List<Data.Images> Images_GetById(string Id)		{			return db.Images_GetById(Id);		}		#endregion		#region[Images_GetByTop]		public static List<Data.Images> Images_GetByTop(string Top, string Where, string Order)		{			return db.Images_GetByTop(Top, Where, Order);		}		#endregion		#region[Images_GetByAll]		public static List<Data.Images> Images_GetByAll()		{			return db.Images_GetByAll();		}		#endregion		#region[Images_Insert]		public static bool Images_Insert(Images data)		{			return db.Images_Insert(data);		}		#endregion		#region[Images_Update]		public static bool Images_Update(Images data)		{			return db.Images_Update(data);		}		#endregion		#region[Images_Delete]		public static bool Images_Delete(string Id)		{			return db.Images_Delete(Id);		}		#endregion	}}