using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class CommentNews	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Address;		private string  _Email;		private string  _Level;		private string  _Detail;		private string  _NewsID;		private string  _Ord;		private string  _Active;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Address{ get { return _Address; } set { _Address = value; } }		public string Email{ get { return _Email; } set { _Email = value; } }		public string Level{ get { return _Level; } set { _Level = value; } }		public string Detail{ get { return _Detail; } set { _Detail = value; } }		public string NewsID{ get { return _NewsID; } set { _NewsID = value; } }		public string Ord{ get { return _Ord; } set { _Ord = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		#endregion		#region[CommentNews IDataReader]		public CommentNews CommentNewsIDataReader(IDataReader dr)		{			Data.CommentNews obj = new Data.CommentNews();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Address = (dr["Address"] is DBNull) ? string.Empty : dr["Address"].ToString();			obj.Email = (dr["Email"] is DBNull) ? string.Empty : dr["Email"].ToString();			obj.Level = (dr["Level"] is DBNull) ? string.Empty : dr["Level"].ToString();			obj.Detail = (dr["Detail"] is DBNull) ? string.Empty : dr["Detail"].ToString();			obj.NewsID = (dr["NewsID"] is DBNull) ? string.Empty : dr["NewsID"].ToString();			obj.Ord = (dr["Ord"] is DBNull) ? string.Empty : dr["Ord"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			return obj;		}		#endregion	}}