using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class Config	{		#region[Declare variables]		private string  _Id;		private string  _Mail_Smtp;		private string  _Mail_Port;		private string  _Mail_Info;		private string  _Mail_Noreply;		private string  _Mail_Password;		private string  _Contact;		private string  _Copyright;		private string  _Title;		private string  _Description;		private string  _Keyword;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Mail_Smtp{ get { return _Mail_Smtp; } set { _Mail_Smtp = value; } }		public string Mail_Port{ get { return _Mail_Port; } set { _Mail_Port = value; } }		public string Mail_Info{ get { return _Mail_Info; } set { _Mail_Info = value; } }		public string Mail_Noreply{ get { return _Mail_Noreply; } set { _Mail_Noreply = value; } }		public string Mail_Password{ get { return _Mail_Password; } set { _Mail_Password = value; } }		public string Contact{ get { return _Contact; } set { _Contact = value; } }		public string Copyright{ get { return _Copyright; } set { _Copyright = value; } }		public string Title{ get { return _Title; } set { _Title = value; } }		public string Description{ get { return _Description; } set { _Description = value; } }		public string Keyword{ get { return _Keyword; } set { _Keyword = value; } }		#endregion		#region[Config IDataReader]		public Config ConfigIDataReader(IDataReader dr)		{			Data.Config obj = new Data.Config();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Mail_Smtp = (dr["Mail_Smtp"] is DBNull) ? string.Empty : dr["Mail_Smtp"].ToString();			obj.Mail_Port = (dr["Mail_Port"] is DBNull) ? string.Empty : dr["Mail_Port"].ToString();			obj.Mail_Info = (dr["Mail_Info"] is DBNull) ? string.Empty : dr["Mail_Info"].ToString();			obj.Mail_Noreply = (dr["Mail_Noreply"] is DBNull) ? string.Empty : dr["Mail_Noreply"].ToString();			obj.Mail_Password = (dr["Mail_Password"] is DBNull) ? string.Empty : dr["Mail_Password"].ToString();			obj.Contact = (dr["Contact"] is DBNull) ? string.Empty : dr["Contact"].ToString();			obj.Copyright = (dr["Copyright"] is DBNull) ? string.Empty : dr["Copyright"].ToString();			obj.Title = (dr["Title"] is DBNull) ? string.Empty : dr["Title"].ToString();			obj.Description = (dr["Description"] is DBNull) ? string.Empty : dr["Description"].ToString();			obj.Keyword = (dr["Keyword"] is DBNull) ? string.Empty : dr["Keyword"].ToString();			return obj;		}		#endregion	}}