using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class Services	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Active;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		#endregion		#region[Services IDataReader]		public Services ServicesIDataReader(IDataReader dr)		{			Data.Services obj = new Data.Services();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			return obj;		}		#endregion	}}