using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class Support	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Email;		private string  _Phone;
        private string _Nick;
        private string _Skype;		private string  _Active;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Email{ get { return _Email; } set { _Email = value; } }		public string Phone{ get { return _Phone; } set { _Phone = value; } }
        public string Nick { get { return _Nick; } set { _Nick = value; } }
        public string Skype { get { return _Skype; } set { _Skype = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		#endregion		#region[Support IDataReader]		public Support SupportIDataReader(IDataReader dr)		{			Data.Support obj = new Data.Support();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Email = (dr["Email"] is DBNull) ? string.Empty : dr["Email"].ToString();			obj.Phone = (dr["Phone"] is DBNull) ? string.Empty : dr["Phone"].ToString();
            obj.Nick = (dr["Nick"] is DBNull) ? string.Empty : dr["Nick"].ToString();
            obj.Skype = (dr["Skype"] is DBNull) ? string.Empty : dr["Skype"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			return obj;		}		#endregion	}}