using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class TB_ThongKe	{		#region[Declare variables]		private string  _MaTB;		private string  _ThoiGian;		private string  _SoTruyCap;		#endregion		#region[Public Properties]		public string MaTB{ get { return _MaTB; } set { _MaTB = value; } }		public string ThoiGian{ get { return _ThoiGian; } set { _ThoiGian = value; } }		public string SoTruyCap{ get { return _SoTruyCap; } set { _SoTruyCap = value; } }		#endregion		#region[TB_ThongKe IDataReader]		public TB_ThongKe TB_ThongKeIDataReader(IDataReader dr)		{			Data.TB_ThongKe obj = new Data.TB_ThongKe();			obj.MaTB = (dr["MaTB"] is DBNull) ? string.Empty : dr["MaTB"].ToString();			obj.ThoiGian = (dr["ThoiGian"] is DBNull) ? string.Empty : dr["ThoiGian"].ToString();			obj.SoTruyCap = (dr["SoTruyCap"] is DBNull) ? string.Empty : dr["SoTruyCap"].ToString();			return obj;		}		#endregion	}}