using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class Tags	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _Title;		private string  _Link;		private string  _Ord;		private string  _Active;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string Title{ get { return _Title; } set { _Title = value; } }		public string Link{ get { return _Link; } set { _Link = value; } }		public string Ord{ get { return _Ord; } set { _Ord = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		#endregion		#region[Tags IDataReader]		public Tags TagsIDataReader(IDataReader dr)		{			Data.Tags obj = new Data.Tags();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.Title = (dr["Title"] is DBNull) ? string.Empty : dr["Title"].ToString();			obj.Link = (dr["Link"] is DBNull) ? string.Empty : dr["Link"].ToString();			obj.Ord = (dr["Ord"] is DBNull) ? string.Empty : dr["Ord"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			return obj;		}		#endregion	}}