using System;using System.Data;using System.Data.SqlClient;using System.Collections.Generic;namespace MyWeb.Data{	public class User	{		#region[Declare variables]		private string  _Id;		private string  _Name;		private string  _UserName;		private string  _Password;		private string  _Email;		private string  _Phone;		private string  _Date;		private string  _Admin;		private string  _Active;		#endregion		#region[Public Properties]		public string Id{ get { return _Id; } set { _Id = value; } }		public string Name{ get { return _Name; } set { _Name = value; } }		public string UserName{ get { return _UserName; } set { _UserName = value; } }		public string Password{ get { return _Password; } set { _Password = value; } }		public string Email{ get { return _Email; } set { _Email = value; } }		public string Phone{ get { return _Phone; } set { _Phone = value; } }		public string Date{ get { return _Date; } set { _Date = value; } }		public string Admin{ get { return _Admin; } set { _Admin = value; } }		public string Active{ get { return _Active; } set { _Active = value; } }		#endregion		#region[User IDataReader]		public User UserIDataReader(IDataReader dr)		{			Data.User obj = new Data.User();			obj.Id = (dr["Id"] is DBNull) ? string.Empty : dr["Id"].ToString();			obj.Name = (dr["Name"] is DBNull) ? string.Empty : dr["Name"].ToString();			obj.UserName = (dr["UserName"] is DBNull) ? string.Empty : dr["UserName"].ToString();			obj.Password = (dr["Password"] is DBNull) ? string.Empty : dr["Password"].ToString();			obj.Email = (dr["Email"] is DBNull) ? string.Empty : dr["Email"].ToString();			obj.Phone = (dr["Phone"] is DBNull) ? string.Empty : dr["Phone"].ToString();			obj.Date = (dr["Date"] is DBNull) ? string.Empty : dr["Date"].ToString();			obj.Admin = (dr["Admin"] is DBNull) ? string.Empty : dr["Admin"].ToString();			obj.Active = (dr["Active"] is DBNull) ? string.Empty : dr["Active"].ToString();			return obj;		}		#endregion	}}